
from datetime import datetime
from sys import stderr
from uuid import uuid4, UUID
from sqlmodel import Field, Session, SQLModel, create_engine, select

class User(SQLModel, table=True):
    #Generated by database. Do not declare
    user_id: UUID | None = Field(default_factory=uuid4, primary_key=True)
    username: str
    password: str
    email: str
    phone: str | None
    create_at: datetime | None = Field(default=datetime.now())
    updated_at: datetime = Field(default_factory=datetime.now)

class Post(SQLModel, table=True):
    post_id: UUID | None = Field(default_factory=uuid4, primary_key=True)
    user_id: UUID = Field(foreign_key="user.user_id")
    content: str | None
    create_at: datetime | None = Field(default=datetime.now())
    updated_at: datetime = Field(default_factory=datetime.now)

